import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("bestsellers"); // Default query
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true);
      try {
        // Fetch books from Google Books API
        const response = await axios.get(`/api/books?q=${searchQuery}`);
        console.log("Fetched books:", response.data);
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
        setBooks([]);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, [searchQuery]);

  const handleSearch = (event) => {
    event.preventDefault();
    const query = event.target.elements.query.value.trim();
    if (query) {
      setSearchQuery(query);
    }
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>My Bookshelf</h1>
        <nav>
          <a href="/">Home</a>
          <a href="/dashboard">Dashboard</a>
          <a href="/bookshelf">Bookshelf</a>
        </nav>
      </header>

      <main className="dashboard-main">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            name="query"
            placeholder="Search for books..."
            className="search-input"
          />
          <button type="submit" className="search-button">
            Search
          </button>
        </form>

        <h2>Explore Books</h2>
        {loading ? (
          <p>Loading books...</p>
        ) : (
          <div className="book-grid">
            {books.length > 0 ? (
              books.map((book) => (
                <div key={book.id} className="book-card">
                  <img
                    src={book.image || "https://via.placeholder.com/150"}
                    alt={book.title}
                    className="book-cover"
                  />
                  <div className="book-info">
                    <h3>{book.title}</h3>
                    <p className="book-author">
                      by {book.authors.join(", ")}
                    </p>
                    <p className="book-description">
                      {book.description
                        ? book.description.slice(0, 100) + "..."
                        : "Description not available"}
                    </p>
                    <a
                      href={book.infoLink || "#"}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="book-link"
                    >
                      View More
                    </a>
                  </div>
                </div>
              ))
            ) : (
              <p>No books found. Try a different search query.</p>
            )}
          </div>
        )}
      </main>

      <footer className="dashboard-footer">
        <p>&copy; 2024 My Bookshelf. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Dashboard;
