import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Bookshelf.css";

const Bookshelf = () => {
  const [bookshelf, setBookshelf] = useState([]); // Holds books in "My Bookshelf"
  const [loading, setLoading] = useState(true); // Loading state for better UX
  const [error, setError] = useState(null); // Error state

  // Helper function to get the JWT token
  const getToken = () => localStorage.getItem("jwtToken");

  // Fetch books from "My Bookshelf" on component mount
  useEffect(() => {
    const token = getToken();

    if (!token) {
      setError("You are not authenticated. Please log in.");
      setLoading(false);
      return;
    }

    axios
      .get("/api/my-bookshelf", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setBookshelf(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching bookshelf:", error);

        if (error.response?.status === 401) {
          setError("Your session has expired. Please log in again.");
          localStorage.removeItem("jwtToken"); // Remove invalid token
        } else {
          setError(
            error.response?.data?.error || "Failed to load bookshelf. Please try again."
          );
        }

        setLoading(false);
      });
  }, []);

  // Function to remove a book from "My Bookshelf"
  const removeFromBookshelf = (bookId) => {
    const token = getToken();

    if (!token) {
      setError("You are not authenticated. Please log in.");
      return;
    }

    axios
      .delete(`/api/my-bookshelf/${bookId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(() => {
        setBookshelf((prevBookshelf) => prevBookshelf.filter((book) => book.id !== bookId));
      })
      .catch((error) => {
        console.error("Error removing book:", error);
        setError(
          error.response?.data?.error || "Failed to remove the book. Please try again."
        );
      });
  };

  // Render the component
  return (
    <div className="bookshelf-container">
      <h2>My Bookshelf</h2>
      {loading ? (
        <p>Loading your bookshelf...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : bookshelf.length === 0 ? (
        <p>Your bookshelf is empty. Start adding some books!</p>
      ) : (
        <ul className="bookshelf-grid">
          {bookshelf.map((book) => (
            <li key={book.id} className="book-card">
              <img
                src={book.coverImage || "https://via.placeholder.com/150"}
                alt={book.title}
                className="book-cover"
              />
              <div className="book-details">
                <h4>{book.title}</h4>
                <p>by {book.author}</p>
                <p className="book-description">
                  {book.description
                    ? book.description.slice(0, 100) + "..."
                    : "No description available."}
                </p>
                <button
                  onClick={() => removeFromBookshelf(book.id)}
                  className="remove-button"
                >
                  Remove from Bookshelf
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Bookshelf;
