import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch default books on load
    fetchBooks();
  }, []);

  const fetchBooks = (query = "") => {
    setLoading(true); // Show loading while fetching
    axios
      .get(`/api/books?q=${query}`)
      .then((response) => {
        setBooks(response.data);
        setLoading(false); // Hide loading after fetching
      })
      .catch((error) => {
        console.error("Error fetching books:", error);
        setBooks([]);
        setLoading(false); // Hide loading on error
      });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchBooks(searchQuery); // Fetch books based on search query
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Browse latest collection</h1>
        <nav>
          <a href="/">Home</a>
          <a href="/dashboard">Dashboard</a>
          <a href="/bookshelf">Bookshelf</a>
        </nav>
      </header>

      <main className="dashboard-main">
        <div className="search-bar">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for books..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>

        <h2>Explore Books</h2>
        {loading ? (
          <p>Loading books...</p>
        ) : books.length > 0 ? (
          <div className="book-grid">
            {books.map((book) => (
              <div key={book.id} className="book-card">
                <img
                  src={book.image || "https://via.placeholder.com/150"}
                  alt={book.title}
                  className="book-cover"
                />
                <div className="book-info">
                  <h3>{book.title}</h3>
                  <p className="book-author">by {book.authors.join(", ")}</p>
                  <p className="book-description">
                    {book.description.slice(0, 100)}...
                  </p>
                  <a
                    href={book.infoLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="book-link"
                  >
                    View More
                  </a>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No books found. Try a different search query.</p>
        )}
      </main>

      <footer className="dashboard-footer">
        <p>&copy; 2024 My Bookshelf. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Dashboard;
