import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('/api/currently-reading', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Pass the JWT token if available
          },
        });
        const data = await response.json();

        // Check if data is an array before calling .map
        if (Array.isArray(data)) {
          setBooks(data);
        } else {
          setError('Data is not in expected format.');
        }
      } catch (err) {
        setError('Error fetching books.');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Currently Reading</h1>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <img src={book.coverImage} alt={book.title} />
            <p>{book.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
