// frontend/src/components/Dashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [currentBook, setCurrentBook] = useState(null);
  const [progress, setProgress] = useState(0);
  const [comment, setComment] = useState('');

  useEffect(() => {
    // Simulating fetching user-specific data (Currently Reading book)
    const fetchCurrentBook = () => {
      setCurrentBook({
        title: 'The Great Gatsby',
        author: 'F. Scott Fitzgerald',
      });
    };

    fetchCurrentBook();
  }, []);

  const handleProgressUpdate = (event) => {
    setProgress(event.target.value);
  };

  const handleFinish = () => {
    // Mark book as finished
    setProgress(100);
    setComment('Finished the book!');
  };

  return (
    <div className="dashboard-container">
      <h2>My Dashboard</h2>
      {currentBook ? (
        <div>
          <h3>Currently Reading</h3>
          <p>{currentBook.title} by {currentBook.author}</p>
          <label>Progress: </label>
          <input
            type="number"
            value={progress}
            onChange={handleProgressUpdate}
            max="100"
            min="0"
            step="1"
          />
          <div>
            <label>Comment:</label>
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Write a comment"
            />
          </div>
          <button onClick={handleFinish}>I've Finished</button>
        </div>
      ) : (
        <p>No book is being read currently.</p>
      )}
    </div>
  );
};

export default Dashboard;
