import React, { useState } from "react";
import "./Login.css"; // Add a CSS file for styling

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin({ email, password }); // Pass email and password to the parent component or API
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn">Login</button>
        </form>
        <p className="signup-prompt">
          Don't have an account? <a href="/signup">Sign up</a>
        </p>
        <hr />
        <button className="social-login-btn google">Sign in with Google</button>
        <button className="social-login-btn facebook">Sign in with Facebook</button>
      </div>
    </div>
  );
};

export default Login;
