import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Fetch books from the backend
    axios
      .get("/api/currently-reading") // Correct endpoint for the "currently-reading" route
      .then((response) => {
        console.log("Response data:", response.data); // Log the data to verify it's an array
        if (Array.isArray(response.data)) {
          setBooks(response.data); // Set books if response is an array
        } else {
          console.error("Expected an array but got:", response.data);
          setBooks([]); // Set to an empty array if it's not an array
        }
      })
      .catch((error) => {
        console.error("Error fetching books:", error);
        setBooks([]); // Set to an empty array on error
      });
  }, []);

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>My Bookshelf</h1>
        <nav>
          <a href="/">Home</a>
          <a href="/dashboard">Dashboard</a>
          <a href="/bookshelf">Bookshelf</a>
        </nav>
      </header>

      <main className="dashboard-main">
        <h2>Explore Books</h2>
        <div className="book-grid">
          {books.length > 0 ? (
            books.map((book) => (
              <div key={book.id} className="book-card">
                <img
                  src={book.coverImage || "https://via.placeholder.com/150"}
                  alt={book.title}
                  className="book-cover"
                />
                <div className="book-info">
                  <h3>{book.title}</h3>
                  <p className="book-author">by {book.author}</p>
                  <p className="book-description">
                    {book.description
                      ? book.description.slice(0, 100) + "..."
                      : "Description not available"}
                  </p>

                  <a
                    href={book.link || "#"}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="book-link"
                  >
                    View More
                  </a>
                </div>
              </div>
            ))
          ) : (
            <p>Loading books...</p>
          )}
        </div>
      </main>

      <footer className="dashboard-footer">
        <p>&copy; 2024 Good Reads. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Dashboard;
