// src/components/Navbar.jsx
import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "../styles/Navbar.css";

const Navbar = () => {
  const [userName, setUserName] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const history = useHistory(); // For redirecting after sign out

  useEffect(() => {
    // Get user name from the token or set a default name
    const token = localStorage.getItem("jwtToken");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode JWT token
      setUserName(decodedToken.name || "Punith");
    }
  }, []);

  const handleSignOut = () => {
    localStorage.removeItem("jwtToken"); // Remove token on sign out
    history.push("/login"); // Redirect to login page
  };

  return (
    <header className="navbar">
      <h1>Welcome, {userName}</h1>
      <nav>
        <a href="/dashboard">Dashboard</a>
        <a href="/bookshelf">Bookshelf</a>

        {/* Account Icon and Dropdown */}
        <div className="account-icon" onClick={() => setShowDropdown(!showDropdown)}>
          <img src="https://via.placeholder.com/30" alt="Account" />
          {showDropdown && (
            <div className="account-dropdown">
              <p>Welcome, {userName}</p>
              <button onClick={handleSignOut}>Sign Out</button>
            </div>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
