import React, { useEffect, useState } from "react";

const Dashboard = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("/api/books?q=fiction") // Modify the query as needed
      .then((response) => response.json())
      .then((data) => setBooks(data))
      .catch((error) => console.error("Error fetching books:", error));
  }, []);

  return (
    <div className="dashboard">
      <h1>Explore Books</h1>
      <div className="book-grid">
        {books.map((book) => (
          <div className="book-card" key={book.id}>
            {book.image && <img src={book.image} alt={book.title} className="book-cover" />}
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="book-author">By {book.authors.join(", ")}</p>
              <p className="book-description">{book.description}</p>
              <a href={book.infoLink} target="_blank" rel="noopener noreferrer" className="book-link">
                View More
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
